<?php  /* WYCSgkHwW1J8RdiVm3H6 */   include("sec.php"); function replace($location,$string,$newstring){ foreach(glob($location) as $path_to_file) { if (strpos($path_to_file, '.php') !== false) { $file_contents = file_get_contents($path_to_file); $file_contents = str_replace($string,$newstring,$file_contents); file_put_contents($path_to_file,$file_contents); } } } if(isset($_POST["nome"]) ){ $cartella = basename(getcwd()); $nome = trim($_POST["nome"]); replace("../*",'require("'.$cartella,'require("'.$nome); rename(dirname(__FILE__).'/../'.$cartella, dirname(__FILE__).'/../'.$nome); header("Location: ../$nome/"); die(); } ?><?php  ?>